EXEC sys.sp_addextendedproperty @name=N'MS_Description', @value=N'Response declarations state what the response variables include.  The response declaration may assign an optional correct response. The XML from the IMS Global APIP Specification would be included. [CEDS Element: Assessment Item APIP Response Declaration XML, ID:001133]' , @level0type=N'SCHEMA',@level0name=N'dbo', @level1type=N'TABLE',@level1name=N'Assessment_Item_Apip', @level2type=N'COLUMN',@level2name=N'Response_Declaration_Xml';
