	"Ref_IDEA_Ed_Environment_School_Age_Id" integer  ,
	"Special_Education_FTE" decimal(5,4)  ,
	"Ref_Special_Education_Exit_Reason_Id" integer  ,
	"Special_Education_Services_Exit_Date" date  ,
