 );

COMMENT ON TABLE dbo."Program_Participation_Special_Education" IS 'Information on a person participating in a special education program.';

COMMENT ON COLUMN dbo."Program_Participation_Special_Education"."Organization_Person_Role_Id" IS 'Surrogate Key';

COMMENT ON COLUMN dbo."Program_Participation_Special_Education"."Awaiting_Initial_IDEA_Evaluation_Status" IS 'Awaiting initial evaluation for special education programs and related services under the Individuals with Disabilities Education Act (IDEA).  [CEDS Element: Awaiting Initial IDEA Evaluation Status, ID:000031]';

COMMENT ON COLUMN dbo."Program_Participation_Special_Education"."Ref_IDEA_Educational_Environment_ECId" IS 'The program in which children ages 3 through 5 attend and in which these children receive special education and related services. [CEDS Element: IDEA Educational Environment for Early Childhood, ID:000559]  (Foreign key - Ref_Idea_Educational_Environment)';

COMMENT ON COLUMN dbo."Program_Participation_Special_Education"."Ref_IDEA_Ed_Environment_School_Age_Id" IS 'The setting in which children ages 6 through 21, receive special education and related services. [CEDS Element: IDEA Educational Environment for School Age, ID:000535]  (Foreign key - Ref_Idea_Educational_Environment_School_Age)';

COMMENT ON COLUMN dbo."Program_Participation_Special_Education"."Special_Education_FTE" IS 'Calculated ratio of time the student is in a special education setting. Values range from 0.00 to 1.00. If the student is in a special education setting 25% of the time, the value is .25; if 100% of the time, the value is 1.00. [CEDS Element: Special Education Full Time Equivalency, ID:001242]';

COMMENT ON COLUMN dbo."Program_Participation_Special_Education"."Ref_Special_Education_Exit_Reason_Id" IS 'The reason children who were in special education at the start of the reporting period were not in special education at the end of the reporting period. [CEDS Element: Special Education Exit Reason, ID:000260] (Foreign key - Ref_Special_Education_Exit_Reason)';

COMMENT ON COLUMN dbo."Program_Participation_Special_Education"."Special_Education_Services_Exit_Date" IS 'The date a child with disabilities (IDEA) ages 14 through 21 exited special education. [CEDS Element: Special Education Services Exit Date, ID:000263]';

CREATE TABLE dbo."Program_Participation_Title_III_Lep" ( 
